import { Component, Input, OnInit } from '@angular/core';
//Para que funcione deberemos de importar Input y OnInit 
@Component({
  selector: 'app-listado-generico',
  templateUrl: './listado-generico.component.html',
  styleUrls: ['./listado-generico.component.css']
})
//Para que funcione deberemos de agregar implements On Init ya que viene vacio e 
// @Input()
// listado;
// constructor(){}
// ngOnInit(): void { 
// }
export class ListadoGenericoComponent implements OnInit{

  @Input()
  listado;
  constructor(){}

  ngOnInit(): void {

  }

}
<div *ngIf="!listado">
    <span #cargando>
        <ng-content select="[cargando]"></ng-content>
    </span>

<span *ngIf="!cargando.innerHTML.trim()">
    <img src="https://usagif.com/wp-content/uploads/loading-98.gif">
</span>
</div>

<div *ngIf="listado?.length === 0">
    <span #vacio><ng-content select="[vacio]"></ng-content></span>
        <span *ngIf="!vacio.innerHTML.trim()">  
            <p>No hay películas para mostrar</p>
        </span>
</div>

<!--ng-container sirve para que no se muestre en el HTML al ser inspecionado-->
<ng-container *ngIf="listado && listado.length > 0">
    <ng-content select="[contenido]"></ng-content>

</ng-container>
