import { Component, Inject, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-crear-genero',
  templateUrl: './crear-genero.component.html',
  styleUrls: ['./crear-genero.component.css']
})
export class CrearGeneroComponent implements OnInit {

  constructor(private router: Router, private formBuilder: FormBuilder){}

  form: FormGroup;

  ngOnInit(): void {
    this.form = this.formBuilder.group({
      nombre: ['', {
        validators: [Validators.required, Validators.minLength(3)]
      }]
    });
  }

  guardarCambios(){
    //... guardar los cambios en la DB
    this.router.navigate(['/generos'])
  }

  // obtenerErrorCampoNombre(){
  //   var campo = this.form.get('nombre');
  //   if (campo.hasError('required')){
  //     return 'El campo nombre es requerido';
  //   }

  //   if (campo.hasError('minlength')){
  //     return 'La longitud mínima es de 3 caracteres';
  //   }
  //   return '';
  // }

}
<h2>Crear Género</h2>

<form (submit)="guardarCambios()" [formGroup]="form">
    <mat-form-field appearance="outline">
        <mat-label>Nombre</mat-label>
        <input formControlName="nombre" matInput/>
        <!-- <mat-error *ngIf="form.invalid">{{obtenerErrorCampoNombre}}</mat-error> -->
    </mat-form-field>
    <div>
        <button mat-flat-button color="primary" [disabled]="form.invalid">
            Guardar Cambios
        </button>
        <a mat-stroked-button routerLink="/generos">
            Cancelar
        </a>
    </div>
</form>
